package id.ststech.variables;

/**
 * @author sts
 *
 */
public class MyApplication {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Welcome to Java");
		
		// This is an integer variabel
		int anInteger = 50;
		
		// This is a long variabel
		long aLong = 78L;
		
		// This is a boolean, contains true or false
		boolean aBoolean = true;
		
		// This is a floating-numbers
		float aFloat = 6.7f;
		
		// Variables below are double
		double aDouble = 6.8;
		double aDoubleToo = 6.8D;
		
		// This is a character
		char aChar = 'K';
		
		// This is a String
		String name = "Ali Irawan";
		
		// Try to print those variables above
		System.out.println(anInteger);
		System.out.println(aLong);
		System.out.println(aBoolean);
		System.out.println(aFloat);
		System.out.println(aDouble);
		System.out.println(aDoubleToo);
		System.out.println(aChar);
		System.out.println(name);
		
		// This variable not initialized, local variable no default value
		int someNumber;
		
		// System.out.println(someNumber);
	}

}
